package Example1;

public class MethodsEx {
int a=10,b=8;
public void sum(){
	int c=a+b;
	System.out.println(c);
}
public void sub(){
	int d=a-b;
	System.out.println(d);
}
public void mul(){
	int e=a*b;
	System.out.println(e);
}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		MethodsEx m=new MethodsEx();
	m.sum();
		m.sub();
		m.mul();

	}

}
